import React from "react";
import { ActivityIndicator, StyleSheet, View, Alert } from "react-native";
import WebView from "react-native-webview";
import useFetchUrl from "../../hooks/DynamicWebViewHook/useFetchUrl";


interface DynamicWebOpenerProps {
  data: {
    [key: string]: any;
  };
}

const DynamicWebOpener: React.FC<DynamicWebOpenerProps> = ({ data }) => {
  const { webUrl, loading, error } = useFetchUrl(data);

  if (error) {
    Alert.alert("Error", "Failed to load web content.");
    console.error("Failed to load web content:", error);
  }

  return loading ? (
    <ActivityIndicator size="large" color="#0000ff" />
  ) : webUrl ? (
    // @ts-ignore
    <WebView source={{ uri: webUrl }} style={styles.webview} />
  ) : null;
};

const styles = StyleSheet.create({
  webview: {
    flex: 1,
  },
});

export default DynamicWebOpener;
