import RNFS from 'react-native-fs';

export const createFile = async (filePath: string, content: string): Promise<string | null> => {
  try {
    const fullPath = getFilePath(filePath);
    await RNFS.writeFile(fullPath, content, 'utf8');
    console.log('File created successfully at:', fullPath);
    return fullPath;
  } catch (error) {
    console.error('Error writing file:', error);
    return null;
  }
};

export const readFile = async (filePath: string): Promise<string | null> => {
  try {
    const fullPath = getFilePath(filePath);
    const content = await RNFS.readFile(fullPath, 'utf8');
    console.log('File read successfully!');
    return content;
  } catch (error) {
    console.error('Error reading file:', error);
    return null;
  }
};

export const getFilePath = (fileName: string): string => {
  return `${RNFS.DocumentDirectoryPath}/${fileName}`;
};
